from ch13.code.ch13_03 import integer_mass_table, mass_table_reverse
import numpy as np


def prefix_peptide(peptide):
    parts = []
    masses = []
    # prefix
    for j in range(len(peptide) + 1):
        parts.append(peptide[0:j])
        masses.append(sum([integer_mass_table[x] for x in parts[-1]]))
    return sorted(masses), parts


def peptide_to_peptide_vector(peptide):
    masses = prefix_peptide(peptide)[0]
    masses.remove(0)
    vector = np.zeros(masses[-1], dtype=int)
    for i in masses:

        vector[i-1] = 1
    return vector


def peptide_vector_to_peptide(vector):
    max_mass = len(vector) // 2 + 1
    masses = [0] + [i+1 for i in range(len(vector)) if vector[i] == 1]
    print(masses)
    peptide = ''
    for i in range(len(masses) - 1):
        peptide += mass_table_reverse[masses[i+1] - masses[i]]
    return peptide


